{"pigql":"q = load "<ServiceCase>";
q = filter q by IsClosed=="true"; q = filter q by {{cell(Time_Selection.selection, 0, "Current").asDateRange("date('ClosedDate_Year','ClosedDate_Month','ClosedDate_Day')")}}; 
hasArticle = filter q by 'ArticleVersion' > 0;  
c = group hasArticle by 'Priority', q by 'Priority'; 
c = foreach c generate count(hasArticle)/count(q)  as 'avg_AttachedRate', hasArticle['Priority'] as 'Priority'; c = order c by 'avg_AttachedRate' {{cell(static_1.selection, 0, "value").asString()}};","measures":[["count","*","avg_AttachedRate"]],"measuresMap":{},"autoFilter":true,"groups":["Priority"]}
